import { createClient } from "@supabase/supabase-js";

const supabaseUrl =
  process.env.REACT_APP_SUPABASE_URL || "https://placeholder.supabase.co";
const supabaseKey =
  process.env.REACT_APP_SUPABASE_ANON_KEY || "placeholder-key";

export const supabase = createClient(supabaseUrl, supabaseKey);

// Mock auth functions (will work without real Supabase for now)
export const signUp = async (email, password, fullName) => {
  // Temporary: store in localStorage
  const user = { email, fullName, id: Date.now() };
  localStorage.setItem("user", JSON.stringify(user));
  return { data: { user }, error: null };
};

export const signIn = async (email, password) => {
  // Temporary: check localStorage
  const stored = localStorage.getItem("user");
  if (stored) {
    const user = JSON.parse(stored);
    if (user.email === email) {
      return { data: { user }, error: null };
    }
  }
  return { data: null, error: { message: "Invalid credentials" } };
};

export const signOut = async () => {
  localStorage.removeItem("user");
  return { error: null };
};

export const getCurrentUser = async () => {
  const stored = localStorage.getItem("user");
  if (stored) {
    return JSON.parse(stored);
  }
  return null;
};

// Services functions
export const getServices = async () => {
  return [];
};

// Orders functions
export const createOrder = async (orderData) => {
  return orderData;
};

export const getOrderByNumber = async (orderNumber) => {
  return null;
};
