import React, { useState } from "react";

const WordCounter = ({ text, onTextChange, onFileUpload }) => {
  const [fileName, setFileName] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  const countWords = (str) => {
    if (!str || str.trim() === "") return 0;
    return str.trim().split(/\s+/).length;
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setFileName(file.name);
    setIsProcessing(true);

    try {
      if (file.type === "text/plain") {
        // Handle .txt files
        const text = await file.text();
        onTextChange(text);
        if (onFileUpload) onFileUpload(file);
      } else if (file.type === "application/pdf") {
        // For PDF, we'll need to upload to server for processing
        // For now, just save the file
        alert("PDF uploaded. Word count will be calculated after processing.");
        if (onFileUpload) onFileUpload(file);
      } else if (
        file.type ===
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document" ||
        file.name.endsWith(".docx")
      ) {
        // For DOCX, we'll need to upload to server for processing
        alert(
          "Word document uploaded. Word count will be calculated after processing.",
        );
        if (onFileUpload) onFileUpload(file);
      } else {
        alert(
          "Unsupported file type. Please upload .txt, .pdf, or .docx files.",
        );
      }
    } catch (error) {
      console.error("Error processing file:", error);
      alert("Error processing file. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  const wordCount = countWords(text);

  const styles = {
    container: {
      marginBottom: "2rem",
    },
    label: {
      display: "block",
      fontSize: "1.1rem",
      fontWeight: "600",
      color: "#5A3A79",
      marginBottom: "0.5rem",
    },
    tabContainer: {
      display: "flex",
      gap: "1rem",
      marginBottom: "1rem",
      borderBottom: "2px solid #ddd",
    },
    tab: {
      background: "none",
      border: "none",
      padding: "0.8rem 1.5rem",
      fontSize: "1rem",
      fontWeight: "600",
      cursor: "pointer",
      color: "#6B4A8A",
      borderBottom: "3px solid transparent",
      transition: "all 0.3s",
    },
    activeTab: {
      borderBottom: "3px solid #6B4A8A",
    },
    textarea: {
      width: "100%",
      minHeight: "400px",
      padding: "1rem",
      fontSize: "1rem",
      fontFamily: "inherit",
      border: "2px solid #ddd",
      borderRadius: "8px",
      resize: "vertical",
      transition: "border-color 0.3s",
      lineHeight: "1.6",
    },
    fileUploadArea: {
      border: "2px dashed #6B4A8A",
      borderRadius: "8px",
      padding: "3rem",
      textAlign: "center",
      background: "#F0E8F8",
      cursor: "pointer",
      transition: "all 0.3s",
    },
    fileInput: {
      display: "none",
    },
    uploadIcon: {
      fontSize: "3rem",
      marginBottom: "1rem",
    },
    uploadText: {
      fontSize: "1.1rem",
      color: "#5A3A79",
      marginBottom: "0.5rem",
    },
    uploadSubtext: {
      fontSize: "0.9rem",
      color: "#6b7e85",
    },
    fileInfo: {
      marginTop: "1rem",
      padding: "1rem",
      background: "#e8f5e9",
      borderRadius: "8px",
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
    },
    fileName: {
      fontWeight: "600",
      color: "#2e7d32",
    },
    removeButton: {
      background: "#dc3545",
      color: "white",
      border: "none",
      padding: "0.4rem 1rem",
      borderRadius: "4px",
      cursor: "pointer",
      fontSize: "0.9rem",
    },
    counter: {
      marginTop: "0.5rem",
      fontSize: "1.1rem",
      fontWeight: "600",
      color: "#6B4A8A",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
    },
    processingText: {
      color: "#f39c12",
      fontSize: "0.9rem",
    },
  };

  const [activeTab, setActiveTab] = useState("paste");

  return (
    <div style={styles.container}>
      <label style={styles.label}>Your AI-Generated Content</label>

      <div style={styles.tabContainer}>
        <button
          style={{
            ...styles.tab,
            ...(activeTab === "paste" ? styles.activeTab : {}),
          }}
          onClick={() => setActiveTab("paste")}
        >
          üìù Paste Text
        </button>
        <button
          style={{
            ...styles.tab,
            ...(activeTab === "upload" ? styles.activeTab : {}),
          }}
          onClick={() => setActiveTab("upload")}
        >
          üìé Upload File
        </button>
      </div>

      {activeTab === "paste" ? (
        <textarea
          style={styles.textarea}
          value={text}
          onChange={(e) => onTextChange(e.target.value)}
          placeholder="Paste your AI-generated text here... You can include formatting, paragraphs, bullet points, etc."
        />
      ) : (
        <div>
          <label htmlFor="file-upload" style={styles.fileUploadArea}>
            <input
              id="file-upload"
              type="file"
              accept=".txt,.pdf,.doc,.docx"
              style={styles.fileInput}
              onChange={handleFileUpload}
            />
            <div style={styles.uploadIcon}>üìÑ</div>
            <div style={styles.uploadText}>
              {isProcessing
                ? "Processing file..."
                : "Click to upload or drag and drop"}
            </div>
            <div style={styles.uploadSubtext}>
              Supports: .txt, .pdf, .doc, .docx (Max 10MB)
            </div>
          </label>

          {fileName && (
            <div style={styles.fileInfo}>
              <span style={styles.fileName}>üìÑ {fileName}</span>
              <button
                style={styles.removeButton}
                onClick={() => {
                  setFileName("");
                  onTextChange("");
                }}
              >
                Remove
              </button>
            </div>
          )}
        </div>
      )}

      <div style={styles.counter}>
        <span>Word Count: {wordCount}</span>
        {isProcessing && (
          <span style={styles.processingText}>Processing...</span>
        )}
      </div>
    </div>
  );
};

export default WordCounter;
