import React, { useState } from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import WordCounter from "../../components/order/WordCounter";
import PricingCalculator from "../../components/order/PricingCalculator";
import OrderForm from "../../components/order/OrderForm";

const OrderPage = () => {
  const [text, setText] = useState("");
  const [service, setService] = useState("");
  const [tier, setTier] = useState("");
  const [addOns, setAddOns] = useState({
    rushDelivery: false,
    extraRevision: false,
    enhancedFactCheck: false,
  });
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
  });

  const countWords = (str) => {
    if (!str || str.trim() === "") return 0;
    return str.trim().split(/\s+/).length;
  };

  const handleSubmit = () => {
    const wordCount = countWords(text);

    if (!text || wordCount === 0) {
      alert("Please paste your text first");
      return;
    }
    if (!service || !tier) {
      alert("Please select service and tier");
      return;
    }
    if (!formData.name || !formData.email) {
      alert("Please fill in your name and email");
      return;
    }

    console.log("Order submitted:", {
      text,
      wordCount,
      service,
      tier,
      addOns,
      formData,
    });

    alert("Order submitted! (Payment integration coming soon)");
  };

  const styles = {
    page: {
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
    },
    container: {
      maxWidth: "1200px",
      margin: "0 auto",
      padding: "3rem 2rem",
      flexGrow: 1,
    },
    title: {
      fontSize: "clamp(2rem, 5vw, 3rem)",
      textAlign: "center",
      color: "#5A3A79",
      marginBottom: "3rem",
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: "2rem",
    },
    column: {
      display: "flex",
      flexDirection: "column",
      gap: "2rem",
    },
  };

  return (
    <div style={styles.page}>
      <Header />

      <div style={styles.container}>
        <h1 style={styles.title}>Place Your Order</h1>

        <div style={styles.grid}>
          <div style={styles.column}>
            <WordCounter text={text} onTextChange={setText} />
            <OrderForm
              formData={formData}
              onFormChange={setFormData}
              onSubmit={handleSubmit}
            />
          </div>

          <div style={styles.column}>
            <PricingCalculator
              service={service}
              tier={tier}
              wordCount={countWords(text)}
              addOns={addOns}
              onServiceChange={setService}
              onTierChange={setTier}
              onAddOnsChange={setAddOns}
            />
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default OrderPage;
