import React from "react";

const WordCounter = ({ text, onTextChange }) => {
  const countWords = (str) => {
    if (!str || str.trim() === "") return 0;
    return str.trim().split(/\s+/).length;
  };

  const wordCount = countWords(text);

  const styles = {
    container: {
      marginBottom: "2rem",
    },
    label: {
      display: "block",
      fontSize: "1.1rem",
      fontWeight: "600",
      color: "#5A3A79",
      marginBottom: "0.5rem",
    },
    textarea: {
      width: "100%",
      minHeight: "200px",
      padding: "1rem",
      fontSize: "1rem",
      fontFamily: "inherit",
      border: "2px solid #ddd",
      borderRadius: "8px",
      resize: "vertical",
      transition: "border-color 0.3s",
    },
    counter: {
      marginTop: "0.5rem",
      fontSize: "1rem",
      fontWeight: "600",
      color: "#6B4A8A",
    },
  };

  return (
    <div style={styles.container}>
      <label style={styles.label}>Paste Your AI-Generated Text</label>
      <textarea
        style={styles.textarea}
        value={text}
        onChange={(e) => onTextChange(e.target.value)}
        placeholder="Paste your AI-generated text here..."
      />
      <div style={styles.counter}>Word Count: {wordCount}</div>
    </div>
  );
};

export default WordCounter;
