import React, { useState } from "react";
import LandingPage from "./LandingPage";
import AuthModal from "./AuthModal";
import HumanizerApp from "./HumanizerApp";
import AdminDashboard from "./AdminDashboard";

// Theme Configuration (DRY)
export const theme = {
  colors: {
    primary: "#2563eb",
    primaryDark: "#1e40af",
    primaryLight: "#3b82f6",
    textPrimary: "#60a5fa", // Light blue for main text
    textSecondary: "#93c5fd", // Lighter blue for secondary text
    textDark: "#1e40af", // Dark blue for emphasis
    success: "#10b981",
    warning: "#f59e0b",
    danger: "#ef4444",
    gray: {
      50: "#f9fafb",
      100: "#f3f4f6",
      200: "#e5e7eb",
      300: "#d1d5db",
      400: "#9ca3af",
      500: "#6b7280",
      600: "#4b5563",
      700: "#374151",
      800: "#1f2937",
      900: "#111827",
    },
  },
};

const App = () => {
  const [currentView, setCurrentView] = useState("landing"); // landing, app, admin
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [authMode, setAuthMode] = useState("login"); // login or signup
  const [user, setUser] = useState(null); // null when logged out

  // Mock login/signup (replace with Supabase auth)
  const handleAuth = (email, password, isSignup) => {
    // TODO: Replace with Supabase authentication
    setUser({
      email,
      name: email.split("@")[0],
      credits: 100,
    });
    setShowAuthModal(false);
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentView("landing");
  };

  const openAuth = (mode = "login") => {
    setAuthMode(mode);
    setShowAuthModal(true);
  };

  return (
    <>
      {currentView === "landing" && (
        <LandingPage
          onGetStarted={() => setCurrentView("app")}
          onLogin={() => openAuth("login")}
          onSignup={() => openAuth("signup")}
          user={user}
          onLogout={handleLogout}
        />
      )}

      {currentView === "app" && (
        <HumanizerApp
          user={user}
          onLogin={() => openAuth("login")}
          onBackToHome={() => setCurrentView("landing")}
        />
      )}

      {currentView === "admin" && (
        <AdminDashboard onBackToHome={() => setCurrentView("landing")} />
      )}

      <AuthModal
        isOpen={showAuthModal}
        onClose={() => setShowAuthModal(false)}
        mode={authMode}
        onSubmit={handleAuth}
        onSwitchMode={() =>
          setAuthMode(authMode === "login" ? "signup" : "login")
        }
      />
    </>
  );
};

export default App;
