import React, { useEffect, useState } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { createOrder, createNotification } from "../services/supabase";

const PaymentPage = () => {
  const [searchParams] = useSearchParams();
  const [processing, setProcessing] = useState(false);
  const navigate = useNavigate();

  const amount = searchParams.get("amount");

  const handleSimulatePayment = async () => {
    setProcessing(true);

    // Get order data from session
    const orderDataString = sessionStorage.getItem("orderToCreate");
    if (!orderDataString) {
      alert("Order data not found. Please start over.");
      navigate("/order");
      return;
    }

    const orderData = JSON.parse(orderDataString);

    // Add initial status
    orderData.status = "in-progress";
    orderData.payment_status = "paid";

    // Create the order NOW (after "payment")
    const result = await createOrder(orderData);

    if (result) {
      // Clear session
      sessionStorage.removeItem("orderToCreate");
      sessionStorage.removeItem("pendingOrder");

      // Notify customer
      await createNotification(
        orderData.customer_email,
        "new_order",
        "Order Placed Successfully! ðŸŽ‰",
        `Your order #${result.order_number} has been received and is being processed.`,
        "/my-orders",
      );

      // Notify admin (replace with your email)
      await createNotification(
        "YOUR_ADMIN_EMAIL@gmail.com",
        "new_order",
        `New Order: #${result.order_number}`,
        `${orderData.customer_email} placed a new order. ${orderData.word_count} words, $${orderData.total_price}`,
        "/admin",
      );

      console.log("âœ… Order created:", result.order_number);

      setTimeout(() => {
        alert("âœ… Payment Successful! Your order is being processed.");
        navigate("/my-orders");
      }, 2000);
    } else {
      alert("Failed to create order");
      setProcessing(false);
    }
  };

  const styles = {
    page: { minHeight: "100vh", display: "flex", flexDirection: "column" },
    container: {
      maxWidth: "600px",
      margin: "10rem auto",
      padding: "0 2rem",
      textAlign: "center",
    },
    card: {
      background: "white",
      borderRadius: "16px",
      padding: "3rem",
      boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
    },
    title: { fontSize: "2rem", color: "#5A3A79", marginBottom: "1rem" },
    amount: {
      fontSize: "3rem",
      fontWeight: "700",
      color: "#6B4A8A",
      marginBottom: "2rem",
    },
    button: {
      background: "#50ADB5",
      color: "white",
      border: "none",
      padding: "1.2rem 3rem",
      borderRadius: "30px",
      fontSize: "1.2rem",
      fontWeight: "700",
      cursor: "pointer",
      width: "100%",
      marginTop: "1rem",
    },
    spinner: {
      width: "60px",
      height: "60px",
      border: "6px solid #f0f0f0",
      borderTop: "6px solid #6B4A8A",
      borderRadius: "50%",
      animation: "spin 1s linear infinite",
      margin: "0 auto 2rem",
    },
  };

  if (processing) {
    return (
      <div style={styles.page}>
        <Header />
        <div style={styles.container}>
          <div style={styles.spinner} />
          <h1 style={styles.title}>Processing Payment...</h1>
        </div>
        <Footer />
        <style>{`@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); }}`}</style>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <Header />
      <div style={styles.container}>
        <div style={styles.card}>
          <h1 style={styles.title}>Complete Payment</h1>
          <div style={styles.amount}>${amount}</div>
          <button style={styles.button} onClick={handleSimulatePayment}>
            ðŸ§ª Simulate Payment
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default PaymentPage;
